import React, {Component } from 'react';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardActions, Button, CardMenu, IconButton } from 'react-mdl';

class Projects extends Component {
    constructor(props) {
        super(props);
        this.state = { activeTab: 0 };
    }
toggleCategories(){

    if(this.state.activeTab === 0){
        return(
            <Card shadow={5} style={{minWidth: '50', margin: 'auto'}}>
                <CardTitle style={{color: '#fff', height: '176px', backgr}}></CardTitle>

            </Card>
        )
    } else if (this.state.activeTab === 1) {
        return(
            <div><h1>This is Node JS</h1></div>
        )
    } else if (this.state.activeTab === 2) {
        return(
            <div><h1>This is Javascript</h1></div>
        )
    } else if (this.state.activeTab === 3) {
        return(
            <div><h1>This is Express</h1></div>
        )
    }
}

    render() {
        return(
            <div className="category-tabs">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
                    <Tab>React</Tab>
                    <Tab>Node JS</Tab>
                    <Tab>Javascript</Tab>
                    <Tab>Express</Tab>
                </Tabs>

                <section className="projects-grid">
                    <Grid className="projects-grid">
                        <Cell col={12}>
                            <div className="content">{this.toggleCategories()}</div>
                        </Cell>
                    </Grid>
                </section>
            </div>
        )
    }
}

export default Projects;
